mapper.select:
SELECT COUNT(*) AS CNT
FROM T2_USER_TBL
WHERE U_ID = #{userId}

SELECT *
FROM T2_USER_TBL
WHERE U_ID = #{userId} AND U_PWD = #{pwd}

SELECT COUNT(*) AS CNT
FROM T2_USER_TBL
WHERE U_ID = #{userId} AND STATUS = #{status}

mapper.update:
