mapper.select:
SELECT *
FROM T2_LEISURE_TBL L
INNER JOIN T2_LEISURE_IMG_TBL I ON L.LEISURE_NO = I.LEISURE_NO
INNER JOIN T2_CODE_TYPE C ON L.LEISURE_KIND = C.C_KIND
WHERE MAIN_YN = 'Y' AND C_TYPE = 'WATER' AND LEISURE_KIND = #{kind}
LIMIT #{startNum}, #{lastNum}

SELECT *
FROM T2_LEISURE_TBL L
INNER JOIN T2_LEISURE_IMG_TBL I ON L.LEISURE_NO = I.LEISURE_NO
INNER JOIN T2_CODE_TYPE C ON L.LEISURE_KIND = C.C_KIND
WHERE MAIN_YN = 'Y' AND C_TYPE = 'GROUND' AND LEISURE_KIND = #{kind}
LIMIT #{startNum}, #{lastNum}

SELECT * FROM T2_LEISURE_TBL L INNER JOIN T2_LEISURE_IMG_TBL I ON L.LEISURE_NO = I.LEISURE_NO INNER JOIN T2_CODE_TYPE C ON L.LEISURE_KIND = C.C_KIND WHERE MAIN_YN = 'Y' AND C_TYPE = 'WATER'
AND LEISURE_NAME LIKE CONCAT('%',#{waterKeyword},'%')
AND LEISURE_KIND = #{leisureKind}
LIMIT #{startNum}, #{lastNum}

SELECT COUNT(*) FROM T2_LEISURE_TBL L INNER JOIN T2_LEISURE_IMG_TBL I ON L.LEISURE_NO = I.LEISURE_NO INNER JOIN T2_CODE_TYPE C ON L.LEISURE_KIND = C.C_KIND WHERE MAIN_YN = 'Y' AND C_TYPE = 'WATER'
AND LEISURE_NAME LIKE CONCAT('%',#{waterKeyword},'%')
AND LEISURE_KIND = #{leisureKind}
