mapper.select:
SELECT * FROM T2_RENT_CAR_TBL C INNER JOIN T2_RENT_IMG_TBL I ON C.RENT_NO = I.RENT_NO WHERE 1=1 AND MAIN_YN = 'Y' AND R_DELYN = 'N'
AND RENT_NAME LIKE CONCAT('%',#{rentCarKeyword},'%')
AND RENT_KIND = #{rentKind}
AND RENT_PRICE BETWEEN #{minPay}*10000 AND #{maxPay}*10000
LIMIT #{startNum}, #{lastNum}

mapper.delete:
DELETE
FROM T2_JJIM_TBL
WHERE PRODUCT_NO = #{rentNo} AND PRODUCT_KIND = 'RENT' AND U_ID = #{uId}

mapper.insert:
INSERT INTO T2_CART_TBL(
    U_ID, PRODUCT_KIND, PRODUCT_NO, PEOPLE, S_RESERVE_DATE, L_RESERVE_DATE
)
SELECT
    #{uId}, 'RENT', #{rentNo}, '1', #{checkInDate}, #{checkOutDate}
FROM T2_CART_TBL
WHERE NOT EXISTS (
    SELECT 1
    FROM T2_CART_TBL
    WHERE PRODUCT_KIND = 'RENT'
      AND PRODUCT_NO = #{rentNo}
)
LIMIT 1
